import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import Button from 'react-bootstrap/Button';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { items: [], nom: '' };
    this.state = { items2: [], prenom: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleSubmit2= this.handleSubmit2.bind(this);
  }

  render() {
    return (
      <div className="App"> 
        <div class="container">
          <form onSubmit={[this.handleSubmit,this.handleSubmit2]}>
            <label htmlFor="new-todo">Nom </label>
            <input
              id="new-todo"
              onChange={this.handleChange}
              value={this.state.nom}
            />

            <label htmlFor="new-todo">
              Prenom
            </label>
            <input
              id="new-todo"
              onChange={this.handleChange2}
              value={this.state.prenom}
            />

            <button class = "btn btn-success">
              Ajouter{this.state.items}
            </button>
          </form>
        <table class="table table-bordered">
          <thead>
            <tr>
              <th></th>
              <th>Nom</th>
              <th>Prenom</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            <tr>
            <TodoList items={this.state.items} />
            <TodoList2 items2={this.state.items2} />
            </tr>
          </tbody>

        </table>
      </div>
    </div>
    );
  }

  handleChange(e) {
    this.setState({ nom: e.target.value });
  }
  // handleChange2(e) {
  //   this.setState({ prenom: e.target.value });
  // }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.nom.length) {
      return;
    }
    const newItem = {
      nom: this.state.nom,
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      nom: ''
    }));
  }

// handleSubmit2(e) {
//   e.preventDefault();
//   if (!this.state.prenom.length) {
//     return;
//   }
//   const newItem = {
//     prenom: this.state.prenom,
//     id: Date.now()
//   };
//   this.setState(state => ({
//     items2: state.items.concat(newItem),
//     prenom: ''
//   }));
// }
}

class TodoList extends React.Component {
  render() {
    return (

       <td>
       {this.props.items.map(item => (
          <li key={item.id}>{item.nom}</li>
        ))}</td>
    );
  }
}
class TodoList2 extends React.Component {
  render() {
    return (
      <td>
       {this.props.items2.map(item => (
          <li key={item.id}>{item.prenom}</li>
        ))}</td>
    );
  }
}
export default App;
