import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { items: [], nom: '' };
    this.state = { items: [], prenom: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleSubmit2 = this.handleSubmit2.bind(this);
  }
  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit.handleSubmit2}>
            <label htmlFor="new-todo">Nom</label>
              <input
                id="new-todo"
                onChange={this.handleChange}
                value={this.state.nom}
              />
            <label htmlFor="new-todo">Prenom</label>
              <input
                id="new-todo"
                onChange={this.handleChange2}
                value={this.state.prenom}
              />
            <button className= "btn btn-success">
              Ajouter{this.state.items.length + 1}
            </button>
        </form>
        <TodoList items={this.state.items} />
      </div>
    );
  }
  handleChange(e) {
    this.setState({ nom: e.target.value });
  }

  handleChange2(e) {
    this.setState({ prenom: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.nom.length) {
      return;
    }
    const newItem = {
      nom: this.state.nom,
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      nom: ''
    }));
  }
  handleSubmit2(e) {
    e.preventDefault();
    if (!this.state.prenom.length) {
      return;
    }
    const newItem = {
      prenom: this.state.text,
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      prenom: ''
    }));
  }

}
class TodoList extends React.Component {
  render() {
    return (
      <div>
      <ul>
        {this.props.items.map(item => (
          <li key={item.id}>{item.nom}</li>
        ))}
      </ul>
      <ul>
      {this.props.items.map(item => (
        <li key={item.id}>{item.prenom}</li>
      ))}
    </ul>
    </div>
    );
  }
}



export default App;
