import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { items: [], nom: '' ,items2 : [], prenom : ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleSubmit2 = this.handleSubmit2.bind(this);
  }
  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit.handleSubmit2}>
            <label htmlFor="new-todo">Nom</label>
              <input
                id="new-todo"
                onChange={this.handleChange}
                value={this.state.nom}
              />
            <label htmlFor="new-todo">Prenom</label>
              <input
                id="new-todo"
                onChange={this.handleChange2}
                value={this.state.prenom}
              />
            <button>
              Ajouter{[this.state.items.length + 1,this.state.items2.length + 1]}
            </button>
        </form>
        <table class="table table-bordered">         
         <thead> 
                 <tr> 
                 <td>id</td> 
                 <td>Nom</td>
                 <td>Prenom</td> 
                 <td>Action</td>
                 </tr> 
         </thead> 
         <tbody> 
                 <tr> 
                 <td></td> 
                 <td></td> 
                 <td></td> 
                 <td><button>X</button> </td> 
                 </tr> 

        </tbody> 
</table>
      </div>
    );
  }
  handleChange(e) {
    this.setState({ nom: e.target.value });
  }

  handleChange2(e) {
    this.setState({ prenom: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.nom.length) {
      return;
    }
    const newItem = {
      nom: this.state.nom,
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      nom: ''
    }));
  }
  handleSubmit2(e) {
    e.preventDefault();
    if (!this.state.prenom.length) {
      return;
    }
    const newItem = {
      prenom: this.state.text,
      id: Date.now()
    };
    this.setState(state => ({
      items2: state.items2.concat(newItem),
      prenom: ''
    }));
  }

}
class TodoList extends React.Component {
  render() {
    return (
      <div>
        <ul>
          {this.props.items.map(item => (
            <li key={item.id}>{item.nom.prenom}</li>
          ))}
        </ul>
      </div>
    );
  }
}



export default App;
