import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';

class App extends Component {


  constructor(props) {
    super(props);
    this.state = { items: [], text: '', items2: [],text2:'' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.handleChange2 = this.handleChange2.bind(this);
    this.handleSubmit2 = this.handleSubmit2.bind(this);
  }





  render() {
    return (
      <div className="App">
         <h3>TODO</h3>
          <TodoList items={this.state.items} />
          <form onSubmit={this.handleSubmit}>
            <label htmlFor="new-todo">
              What needs to be done?
            </label>
            <input
              id="new-todo"
              onChange={this.handleChange}
              value={this.state.text}
            />
            <button>
              Add #{this.state.items.length + 1}
            </button>
          </form>


          <form onSubmit={this.handleSubmit2}>
            <label htmlFor="new-todo2">
              What needs to be done?
            </label>
            <input
              id="new-todo2"
              onChange={this.handleChange}
              value={this.state.text2}
            />
            <button>
              Add #{this.state.items2.length + 1}
            </button>
          </form>
      </div>
    );
  }


 handleChange(e) {
      this.setState({ text: e.target.value });
    }

    handleSubmit(e) {
      e.preventDefault();
      if (!this.state.text.length) {
        return;
      }
      const newItem = {
        text: this.state.text,
        id: Date.now()
      };
      this.setState(state => ({
        items: state.items.concat(newItem),
        text: ''
      }));
    }




    handleChange2(e) {
      this.setState({ text2: e.target.value });
    }

    handleSubmit2(e) {
      e.preventDefault();
      if (!this.state.text2.length) {
        return;
      }
      const newItem2 = {
        text2: this.state.text2,
        id: Date.now()
      };
      this.setState(state => ({
        items: state.items2.concat(newItem2),
        text2: ''
      }));
    }
}

  class TodoList extends React.Component {
    render() {
      return (
        <ul>
          {this.props.items.map(item => (
            <li key={item.id}>{item.text}</li>
          ))}
        </ul>
      );
    }
  }



  class TodoList2 extends React.Component {
    render() {
      return (
        <ul>
          {this.props.items2.map(item2 => (
            <li key={item2.id}>{item2.text2}</li>
          ))}
        </ul>
      );
    }
  }


export default App;
